import axios from 'axios'

function buildFormData (formData, data, parentKey) {
    if (data && typeof data === 'object' && !(data instanceof Date) && !(data instanceof File)) {
        Object.keys(data).forEach(key => {
            buildFormData(formData, data[key], parentKey ? `${parentKey}[${key}]` : key)
        })
    } else {
        const value = data == null ? '' : data

        formData.append(parentKey, value)
    }
}

function convertToFormData (data, additionalMethod) {
    const formData = new FormData()

    buildFormData(formData, data)

    if (additionalMethod) {
        formData.append('_method', additionalMethod)
    }

    return formData
}

function multipartHeaders () {
    return {
        'Content-Type': 'multipart/form-data',
        Accept: 'application/json'
        // 'Authorization': store.getters.user.token
    }
}

const API_SERVER = 'http://127.0.0.1:8889'
// const API_SERVER = ''

const login = axios.create({
    baseURL: API_SERVER + ' /login'
})

export const Login = (cell) => login.delete('/login', cell)
