import axios from 'axios';

const API_SERVER = 'http://127.0.0.1:8889';

const login = axios.create({
  baseURL: API_SERVER,
  withCredentials: true,  // 跨域請求時包含 Cookie
  headers: {
    'Content-Type': 'application/json'
  }
});

export const Login = (cell) => login.post('/login', cell);
